import React, { Component } from 'react'
import SMaterialBlock from './Studycomponents/SMaterialBlock'
// import UserLogin from './Authentication/UserLogin'
import UserSignUp from './Authentication/UserSignup'
import UserLogin from './Authentication/UserLogin'
// import UserLogin from './Authentication/UserLogin'
// import {BrowserRouter, Route} from 'react-router-dom'

export default class App extends Component {
  state = {
    registered: 'login',
  }
  showLoginForm = () =>{
    this.setState({
      registered: 'login',
    })
  }
  showSignupForm = () =>{
    this.setState({
      registered:'signup',
    })
  }
  loggedIn =()=>{
    this.setState({
      registered: 'loggedin'
    })
  }
  render() {
    if (this.state.registered === 'login'){
      return (
        <div>
          {/* <UserSignUp></UserSignUp> */}
            <UserLogin showsignup = {this.showSignupForm}></UserLogin>
            {/* <UserSignUp showlogin = {this.showLoginForm}></UserSignUp> */}
          
          {/* <UserLogin></UserLogin> */}
          {/* <SMaterialBlock></SMaterialBlock> */}
        </div>
      )
    }
    else if(this.state.registered === 'signup'){
      return (
        <div>
          {/* <UserSignUp></UserSignUp> */}
            {/* <UserLogin showsignup = {this.showSignupForm}></UserLogin> */}
            <UserSignUp showlogin = {this.showLoginForm}></UserSignUp>
          
          {/* <UserLogin></UserLogin> */}
          {/* <SMaterialBlock></SMaterialBlock> */}
        </div>
      )
    }
    else if(localStorage.getItem('token')){
      return (
        <div>
          <SMaterialBlock></SMaterialBlock>
        </div>
      )
    }
    else{
      return <p>something went wrong</p>
    }
  }
}
