import React, { Component } from 'react'
import './UserLogin.css'
import * as emailjs from 'emailjs-com'

export default class UserLogin extends Component {
    state = {
        otppassword: '',
        newpassword: '',
        confirmpassword: '',
    }
    componentDidMount = () =>{
        const min =1;
        const max = 1000;
        const rand = min + Math.random() * (max - min);
        const OTPnumber = this.state.random + rand;
        console.log('mail sent to khatri', parseInt(rand))
        let templateParams = {
            from_name: 'bahadurkrishna5555@gmail.com',
            to_name: 'ojhakrishna010@outlook.com',
            subject: 'This is your OTP code',
            message_html: 'Here will be a otp code',
           }

           emailjs.send(
            'gmail',
            'template_jHClttbm',
             templateParams,
             'user_A6IqrUSrMgi0EQb9DLig7'
           ).then(result =>{
               console.log('returned result: ',result)
           }).catch(error =>{
               console.log('error:', error)
           })
    }
    onGoBack = () =>{
        this.props.onshowlogin()
    }
    handleSubmit = (event) =>{

        const formdata = new FormData()
        formdata.append('newpassword', this.state.newpassword)
        fetch('http://127.0.0.1:8000/user', {
        method: 'POST',
        Header: 'context-Type: application/json',
        body: formdata
        }
        ).then((response) =>response.json())
        .then((result)=>{
            console.log('success', result)
            
        })
        .catch((error)=>{
            console.error('Error:',error)
        })
        event.preventDefault();

    }
    onChangeinput =(event)=>{
        this.setState({
            [event.target.name]: event.target.value
        })
    }
 
    render() {
        const {otppassword, newpassword, confirmpassword} = this.state
        return (
            <div>
                <form className = "signin-form" onSubmit = {this.handleSubmit}>
                    <h2>Reset password</h2>
                    <label>OTP Code</label>
                    <input type = "number" name = "otppassword" value = {otppassword} required onChange = {this.onChangeinput}></input>
                    <label>Password</label>
                    <input type = "password" name = "newpassword" value = {newpassword} required onChange = {this.onChangeinput}></input>
                    <label>Confirm Password</label>
                    <input type = "password" name = "confirmpassword" value = {confirmpassword} required onChange = {this.onChangeinput}></input>
                    <div className = 'btn-section'>
                        <button type = "submit">submit</button>
                        <button type = 'button' onClick = {this.onGoBack}>Cancel</button>
                    </div>
                </form>
            </div>
        )
    }
}
