import React, { Component } from 'react'
import './SMaterialBlock.css'

export default class SMaterialBlock extends Component {
    state = {
        study_data : [],
        color: 'black'

    };
    async componentDidMount() {
        try {
          const res = await fetch('http://127.0.0.1:8000/data/studymaterial_material_list/5'); // fetching the data from api, before the page loaded
          const study_data = await res.json();
          console.log(study_data)
          this.setState({
            study_data
          });
        } catch (e) {
          console.log(e);
        }
      }

      onClickAll=()=>{
        this.setState({
            color: 'green'
        })
      }
    render() {
        return (
            <div className = "outer-div">
                <h2 className = "study-material">Study Material</h2>
                <div className = "nav">
                    <button onClick = {this.onClickAll}>All</button>
                    <button>Files</button>
                    <button>videos</button>
                </div>
                <div className = 'study-content-div'>
                    {
                        this.state.study_data.map(item =>(

                            <div className = 'content'>
                            <img src="https://img.icons8.com/cute-clipart/64/000000/file.png" alt = ""/>
                            <h3>{item.file_title}</h3>
                            <p>July 5</p>
                    </div>

                        ))
                    }
                </div>
                
                {/* <h1>Study material of dada</h1>
                <hr></hr>
                {
                    this.state.study_data.map(item =>(
                        <div key = {item.id}>
                            <h2>file link : {item.files}</h2>
                            <h2>video link : {item.videos}</h2>
                            <a href = {item.files}>{item.file_title}</a>
                            <a href = {item.videos}>{item.video_title}</a>
                            <embed src = {item.files}></embed>
                            <video width = "400" controls>
                                <source src={item.videos} type = 'video/mp4'></source>
                            </video>
                            <h2>Created_at : {item.created_at}</h2>
                            <hr></hr>
                        </div>
                    ))
                } */}
            </div>
        );
    }
}
